import React, { useEffect } from "react";
import { connect } from "react-redux";
import styles from "../Favoritos/Favorites.module.css";
import * as action from "../../redux/actions";

const Favorite = (props) => {
  const { myFavorites, orderCards, filterCards } = props;

  const funcionOrder = (event) => {
    dispatch(orderCards(event.target.value));
  };

  const Funcionfilter = (event) => {
    dispatch(filterCards(event.target.value));
  };

  return (
    <div>
      <div>
        <select className={styles.select} onChange={funcionOrder}>
          <option value="Descendente">Descendente</option>
          <option value="Ascendente">Ascendente</option>
        </select>
        <select className={styles.select} onChange={Funcionfilter}>
          <option value="Male">Male</option>
          <option value="Female">Female</option>
          <option value="Genderless">Genderless</option>
          <option value="unknown">unknown</option>
        </select>
      </div>

      {myFavorites?.map((personaje) => {
        return (
          <div className={styles.div}>
            <h3>Name : {personaje.name}</h3>
            <h3>Gender: {personaje.gender}</h3>
            <h3>Species: {personaje.species}</h3>
            <img className={styles.imagen} src={personaje.image} />
          </div>
        );
      })}
    </div>
  );
};

const mapStateToProps = (state) => {
  return {
    myFavorites: state.myFavorites,
  };
};

const mapDispatchToProps = (dispatch) => {
  return {
    orderCards: (id) => {
      dispatch(action.orderCards(id));
    },
    filterCards: (gender) => {
      dispatch(action.filterCards(gender));
    },
  };
};

export default connect(mapStateToProps, mapDispatchToProps)(Favorite);
