import Card from "../Card/Card";
import styles from "./Cards.module.css";
import Nav from "../Nav/Nav";
import { useEffect } from "react";
import { useLocation } from "react-router-dom";
export default function Cards({ characters, onClose, onSearch, idUser }) {
  /* es el arreglo de <personajes className=""></personajes> tambien usamos la prop onClose =>FUNCTION*/

  const [prevIdUser, setPrevIdUser] = useState(idUser);

  useEffect(() => {
    if (prevIdUser !== idUser) {
      setcharacters([]);
      setPrevIdUser(idUser);
    }
  }, [idUser]);

  //cuando se monte renderizo las cards
  // cuando se desmonte vacio el array de cards
  // if(idUser !== idUser) {

  //   useEffect(() => {
  //     return () => {
  //       setcharacters([]);
  //     };
  //   }, []);
  // }

  return (
    <>
      <Nav onSearch={onSearch} />
      <div className={styles.contenedor}>
        {characters?.map(
          ({
            id,
            name,
            species,
            gender,
            image /*podia hacer destructuring aqui...*/,
          }) => (
            <Card
              key={id}
              id={id} // es el id de cada personaje del array characters.. se lo pasamos como props a Card..
              name={name}
              species={species}
              gender={gender}
              image={image}
              idUser={idUser}
              onClose={onClose}
            />
          )
        )}
      </div>
    </>
  );
}
