                this.setState(false);
                this.props.deleteFavorite(this.props.id);
            } else {
                this.setState(true);
                this.props.addFavorite(this.props);
            }
        }
        return (<>
        <div className={styles.contenedor}>
            {
   this.state.isFav ? (
