const mapDispatchToProps = (dispatch) => {
    return {
      addFavorite: () => {
        dispatch(addFavorite())
      },
      deleteFavorite: ()=> {
        dispatch(deleteFavorite());
    }
    };
  };



export default connect(null,mapDispatchToProps)(Card);

