import Card from "../Card/Card";
import styles from "./Cards.module.css";
import Nav from "../Nav/Nav";
import { RemplaceHome, VacioHome } from "../../redux/actions";
import { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
export default function Cards({ characters, onClose, onSearch, idUser }) {
  const dispatch = useDispatch();
  const character = useSelector((state) => state.character);
  /* es el arreglo de <personajes className=""></personajes> tambien usamos la prop onClose =>FUNCTION*/

  //cuando se monte renderizo las cards
  // cuando se desmonte vacio el array de cards

  useEffect(() => {
    dispatch(RemplaceHome(characters));
    return () => {
      dispatch(VacioHome()); // cuando se desmonte el componente que se vacie el array de characters
    };
  }, []);

  return (
    <>
      <Nav onSearch={onSearch} />
      <div className={styles.contenedor}>
        {characters?.map(
          ({
            id,
            name,
            species,
            gender,
            image /*podia hacer destructuring aqui...*/,
          }) => (
            <Card
              key={id}
              id={id} // es el id de cada personaje del array characters.. se lo pasamos como props a Card..
              name={name}
              species={species}
              gender={gender}
              image={image}
              idUser={idUser}
              onClose={onClose}
            />
          )
        )}
      </div>
    </>
  );
}
