import { AGREGAR, ELIMINAR, FILTER, ORDER } from "./actions";

const initialState = {
  myFavorites: [],
  allCharacters: [],
};

const reducer = (state = initialState, actions) => {
  /*
const rootReducer = (state = initialState, action) => {
  switch (action.type) {
    case ADD_FAVORITE:
      return {
        ...state,
        myFavorites: [...state.myFavorites, action.payload],
      };

    case REMOVE_FAVORITE:
      return {
        ...state,
        myFavorites: state.myFavorites.filter(
          (char) => char.id !== action.payload
        ),
      };
    case GET_CHARACTER_DETAIL:
      return {
        ...state,
        characterDetail: action.payload,
      };

    case CLEAN_DETAIL:
      return {
        ...state,
        characterDetail: {},
      };

    default:
      return { ...state };
  }
};

*/

  switch (actions.type) {
    case AGREGAR:
      return {
        ...state,
        myFavorites: [...state.allCharacters, actions.payload],
        allCharacters: [...state.allCharacters, actions.payload],
      };
    case ELIMINAR:
      return {
        ...state,
        myFavorites: state.myFavorites.filter(
          (pars) => pars.id !== actions.payload
        ),
      };
    case FILTER:
      return {
        ...state,
        myFavorites: state.allCharacters.filter(
          (pars) => pars.gender === actions.payload
        ),
      };
    case ORDER:
      return {
        ...state,
        myFavorites:
          actions.payload === "Ascendente"
            ? allCharacters.sort((a, b) => a.id < b.id)
            : allCharacters.sort((a, b) => a.id > b.id),
      };
    //lista.sort((a, b) => (a > b ? 1 : a < b ? -1 : 0)
    default:
      return { ...state };
  }
};

/*
words.sort((a, b) => {
  if (a == b) {
    return 0;
  }
  if (a < b) {
    return -1;
  }
  return 1;
});

*/
export default reducer;
