 import { useState } from "react";
 import {useParams} from "react-router-dom";
 import { useEffect } from "react";
 import { Link } from "react-router-dom";

 const Detail = ({name,status,specie,gender,origin,image}) => {
 const {detailid} = useParams;
 const [character,setcharacter] = useState();
 useEffect(() => {
   const URL = "https://be-a-rym.up.railway.app/api";
   const KEY = "2fed7cd4836b.dab7726779e7348bd5ea";
     fetch(`${URL}/character/${id}?key=${KEY}`)
       .then((response) => response.json())
       .then((char) => {
         if (char.name) {
           setcharacter(char);
         } else {
           window.alert("No hay personajes con ese ID");
         }
       })
       .catch((err) => {
         window.alert("No hay personajes con ese ID");
       });
     return setcharacter({});
   }, [detailid]);

   return (
     <div>
         <h2>Name: {name}</h2>
         <h2>Status: {status}</h2>
         <h2>Specie: {specie}</h2>
         <h2>Gender: {gender}</h2>
         <h2>Origin: {origin}</h2>
         <img src={image} />
         <Link to={"/home"}>
         <button>Home</button>
         </Link>
     </div>
   )
 }
 export default Detail;

{/*Este código es el que buscará al personaje de la API cada vez que el componente se monte.*/}
{/*Y luego, cada vez que se desmonte, borrará su información.*/}
