import { Link } from "react-router-dom";
import { addFavorite, deleteFavorite } from "../../redux/actions";
import styles from "./Card.module.css"; // el ./ => esto quiere decir que es en la carpeta... ../ sale de la carpeta y entra en la carpeta que le indiquemos => ../Card/Card.jsx
import { connect, useDispatch } from "react-redux";
import React from "react";
import { useState } from "react";
import { useEffect } from "react";
import axios from "axios";

function Card({ id, name, species, gender, image, onClose, myFavorites }) {
  const [isFav, setIsFav] = useState(false);
  const dispatch = useDispatch();

  const addFavorite = (character) => {
    axios
      .post("http://loscalhost:3001/rickandmorty/fav", character) // character sweia el body que luego lo usamos en nuestro servidor y aparte character es lo que le pasamos como argumento a esta funcion cuando hacemos la funcion handleFavorite ===> character ===> addFavorite({ id, name, species, gender, image }).
      .then((response) => console.log("ok"));
  };

  const deleteFavorite = (id) => {
    axios.delete(`http://loscalhost:3001/rickandmorty/fav/${id}`); // character sweia el body que luego lo usamos en nuestro servidor y aparte character es lo que le pasamos como argumento a esta funcion cuando hacemos la funcion handleFavorite ===> character ===> addFavorite({ id, name, species, gender, image }).
    dispatch(getFavorites());
    alert("card eliminada");
  };
  //dispatch le enviaba informacion a las actiond para que despues la funcion reduce , cambie o modifique el estado global con esa misma informacion

  // useEffect(() => {
  //      for(let i = 0; i<myFavorites.length; i++){
  //               if(myFavorites[i].id === id){
  //                       setIsFav(true);
  //                  }
  //              }
  //          }, [myFavorites]);

  useEffect(() => {
    myFavorites.forEach((fav) => {
      if (fav.id === id) {
        setIsFav(true);
      }
    });
  }, [myFavorites]);

  const handleFavorite = () => {
    if (isFav === true) {
      setIsFav(false);
      deleteFavorite(id); // esto le manda a action ==> {type: delete , payload : id que nos dan por aca..}
    } else {
      setIsFav(true);
      addFavorite({ id, name, species, gender, image });
    }
  };

  return (
    <div className={styles.contenedor}>
      <div className={styles.divBoton}>
        {isFav ? (
          <button className={styles.boton11} onClick={handleFavorite}>
            ‚ù§Ô∏è
          </button>
        ) : (
          <button className={styles.boton11} onClick={handleFavorite}>
            ü§ç
          </button>
        )}
        <button className={styles.boton} onClick={() => onClose(id)}>
          X
        </button>
      </div>
      <Link to={`/detail/${id}`} className={styles.Link}>
        <h2>Nombre: {name}</h2>
      </Link>
      <h2 className={styles.caracter}>Specie: {species}</h2>
      <h2 className={styles.caracter}>Genero: {gender}</h2>
      <img className={styles.img} src={image} alt="" />
    </div>
  );
}

// traemos el state global de redux , esto no los brinda la funcion conecct()..
// y luego esto lo tenemos como props en nuestro componentes Card..

export default Card;

