import React from "react";
import { connect } from "react-redux";
import { orderCards, filterCards } from "../../redux/actions";
import Card from "../Card/Card";
import styles from "../Favoritos/Favorites.module.css";

class Favorite extends React.Component {
  constructor(props) {
    super(props);
  }
  render() {
    const funcionOrder = (event) => {
      this.props.orderCards(event.target.value);
    };

    const Funcionfilter = (event) => {
      this.props.filterCards(event.target.value);
    };

    return (
      <div>
        <div>
          <select className={styles.select} name="name" onChange={funcionOrder}>
            <option value="Descendente">Descendente</option>
            <option value="Ascendente">Ascendente</option>
          </select>
          <select
            className={styles.select}
            name="name"
            onChange={Funcionfilter}
          >
            <option value="Male">Male</option>
            <option value="Female">Female</option>
            <option value="Genderless">Genderless</option>
            <option value="unknown">unknown</option>
          </select>
        </div>

        {this.props.myFavorites.map((personaje) => {
          return (
            <div>
              name = {personaje.name}
              gender = {personaje.gender}
              species= {personaje.species}
              image = {personaje.image}
            </div>
          );
        })}
      </div>
    );
  }
}

const mapStateToProps = (state) => {
  return {
    myFavorites: state.myFavorites,
  };
};

const mapDispatchToProps = (dispatch) => {
  return {
    orderCards: (id) => {
      dispatch(orderCards(id));
    },
    filterCards: (gender) => {
      dispatch(filterCards(gender));
    },
  };
};

export default connect(mapStateToProps, mapDispatchToProps)(Favorite);

