import styles from "./Form.module.css";
import { useState } from "react";


const validacionEmail = (userData,seterrores,errores) => {
    if (!userData.usename || !userData.password) seterrores({ ...errores, usename: "Email vacío" , password: "Contraseña vacia"}); // se pregunta si en la propiedad usename del estado no hay algo, entonces que tire un error el el stado erores diciendo "esta vacio"..
  else {
    if (/^\w+([\.-]?\w+)*@\w+([\.-]?\w+)*(\.\w{3})+$/.test(userData.usename) || /^(?=.*[a-z])(?=.*[A-Z])(?=.*\d)(?=.*[$@$!%*?&])[A-Za-z\d$@$!%*?&]{8,15}/.test(userData.password)) // de lo contrario que verifique que el usename cumpla con ciertos panoramas como : @ , .com , etc.., de lo contrario tirar otro error diciendo "es invalido"
      seterrores({ ...errores, usename: "" , password: "" });
    else seterrores({ ...errores, usename: "Email inválido" , password: "Contraseña invalida" });
  }
}

const Form = () => {
    const [userData, setUserData] = useState({ username: '', password: '' });
    const [errores,seterrores] = useState({ username: '', password: '' });

    const handleInputChange = (event) => {
         const Propiedad = event.target.name; // esto seria el nombre del input osea username o password..
         const value = event.target.value;  // esto seria el valor de la propiedad , osea lo que escribamos en el input..
         setUserData({...userData , [Propiedad] : value})
         validacionEmail({...userData , [Propiedad] : value},seterrores,errores);
     }

return (
    <div  className={styles.div2}>
    <div className={styles.div}>
        <form>
            <div className={styles.div1}>
            <label htmlFor="username">Username: </label>
                <input placeholder="Escribir email aqui..."
                value={userData.username}
                type="text" 
                name="username"
                onChange={ handleInputChange}
                className={errores.username ? styles.error : styles.success} // entonces aca se pregunta si en el estado de errores en gmail hay algo entonces que se ejecute el estilo de la class error , de lo contrario que se ejecute la class que esta todo correcto
                ></input>
                <span>{errores.username}</span> {/*Aca creamos un span que nos mostrara el tipo de error al lado del input*/}

            </div>
            <div className={styles.div1}>
            <label htmlFor="password">Password: </label>
                <input placeholder="Escribir contraseña aqui..."
                value = {userData.password} 
                type="text"
                name="password"
                onChange={ handleInputChange}
                className={errores.password ? styles.error : styles.success}
                ></input>
                <span>{errores.password}</span> 
            </div>
        </form>
        <button type="submit" >Login</button>
    </div>
    </div>
)
}

export default Form;
