 import { useState } from "react";
 import {useParams} from "react-router-dom";
 import { useEffect } from "react";
 import { Link } from "react-router-dom";

 const Detail = () => {
 const {detailId} = useParams();
 const [character,setcharacter] = useState({});
 useEffect(() => {
   const URL = "https://be-a-rym.up.railway.app/api";
   const KEY = "2fed7cd4836b.dab7726779e7348bd5ea";
     fetch(`${URL}/character/${detailId}?key=${KEY}`) // aca estamos asiendo una peticion al servidor con el id que pasamos al valor del input..
       .then((response) => response.json())
       .then((char) => {
         if (char.name) {
           setcharacter(char); // nos trae el personaje con ese id y nos cambia nuestro estado anterior por el nuevo obj del personaje...
         } else {
           window.alert("No hay personajes con ese ID");
         }
       })
       .catch((err) => {
         window.alert("No hay personajes con ese ID");
       });
     return setcharacter({});
   }, [detailId]);

   return (
     <div>
         <h2>Name: {character.name}</h2>
         <h2>Status: {character.status}</h2>
         <h2>Specie: {character.specie}</h2>
         <h2>Gender: {character.gender}</h2>
         <h2>Origin: {character.origin}</h2>
         <img src={character.image} />
         <Link to={"/home"}>
         <button>Home</button>
         </Link>
     </div>
   )
 }
 export default Detail;

{/*Este código es el que buscará al personaje de la API cada vez que el componente se monte.*/}
{/*Y luego, cada vez que se desmonte, borrará su información.*/}
