import styles from "./Form.module.css";
import { useState } from "react";




const validacion1 = (userData,seterrores,errores) => {
    if (!userData.username) seterrores({ ...errores, username: "Email vacio"});
  else {
    if (/^\w+([\.-]?\w+)*@\w+([\.-]?\w+)*(\.\w{3})+$/.test(userData.username))
      seterrores({ ...errores,username: "" });
    else seterrores({ ...errores,username: "Email invalido"});
  }
}
const validacion2 = (userData,seterrores,errores) => {
  if (!userData.password) seterrores({ ...errores, password: "Contraseña vacía"});
else {
  if (/^(?=\w*\d)(?=\w*[A-Z])(?=\w*[a-z])\S{8,16}$/.test(userData.password))
    seterrores({ ...errores, password: ""});
  else seterrores({ ...errores, password: "Contraseña inválida"});
}
}


const Form = ({login}) => {
  const [userData, setUserData] = useState({ username: "", password: "" });
  const [errores,seterrores] = useState({ username: "", password: "" });
  
  const handleInputChange = (event) => {
    const Propiedad = event.target.name; // esto seria el nombre del input osea username o password..
    const value = event.target.value;  // esto seria el valor de la propiedad , osea lo que escribamos en el input..
    setUserData({...userData , [Propiedad] : value})
    if([Propiedad] === userData.username)validacion1({...userData , [Propiedad] : value},seterrores,errores);
    if([Propiedad] === userData.password)validacion2({...userData , [Propiedad] : value},seterrores,errores);
  }
  const handleSubmit = () => {
   login(userData);
  }

return (
    <div  className={styles.div2}>
    <div className={styles.div}>
        <form>
            <div className={styles.div1}>
            <label htmlFor="username">Username: </label>
                <input placeholder="Escribir email aqui..."
                value={userData.username}
                type="text" 
                name="username"
                onChange={ handleInputChange}
                className={!errores.username ? styles.error : styles.correcto} 
                ></input>
                <span className={styles.span}>{errores.username}</span> 

            </div>
            <div className={styles.div1}>
            <label htmlFor="password">Password: </label>
                <input placeholder="Escribir contraseña aqui..."
                value = {userData.password} 
                type="text"
                name="password"
                onChange={ handleInputChange}
                className={!errores.password ? styles.error : styles.correcto}
                ></input>
                <span className={styles.span}>{errores.password}</span> 
            </div>
        </form>
        <button type="submit"  onClick={handleSubmit}>Login</button>
    </div>
    </div>
)
}

