import axios from "axios";
export const ELIMINAR = "ELIMINAR";
export const AGREGAR = "AGREGAR";
export const FILTER = "FILTER";
export const ORDER = "ORDER";

export const addFavorite = () => {
  return (dispatch) => {
    axios
      .post(`http://localhost:3001/rickandmorty/fav`) // creo que fav es el personaje favorito, osea character en este caso...
      .then((character) =>
        dispatch({ type: ELIMINAR, payload: character.data })
      );
  };
};

export const deleteFavorite = (id) => {
  return (dispatch) => {
    axios
      .delete(`http://localhost:3001/rickandmorty/fav/${id}`) // este es el id que le pasamos como parametros a nuestro array de favoritos...
      .then((response) => dispatch({ type: ELIMINAR, payload: response.data }));
  };
};

export const filterCards = (gender) => {
  return { type: FILTER, payload: gender };
};

export const orderCards = (id) => {
  return { type: ORDER, payload: id };
};

